@EnableWebSecurity
public class ResourceServerConfig extends WebSecurityConfigurerAdapter {

    @Override
    protected void configure(HttpSecurity http) throws Exception {
        http.cors().and()
                .authorizeRequests()
                .mvcMatchers("/api/v1/**")             .hasAuthority("SCOPE_fae-client-orders-oauth2")
              //.mvcMatchers("/api/v1/clientorders/**").hasAuthority("SCOPE_fae-client-orders-oauth2")
                .anyRequest().denyAll()
                .and()
                .oauth2ResourceServer()
                .jwt();
    }

}

